INSERT INTO proprietario_table (id, descricao, logradouro, nome_estabelecimento, telefone, url_icone, endereco_id, usuarios_id) VALUES(1, 'A Fazenda Harmonia é um lugar onde pessoas com e sem deficiência podem desfrutar de um ambiente rural acolhedor e inclusivo. Com infraestrutura adaptada e equipe especializada, a fazenda oferece atividades e experiências diversas, como passeios a cavalo, trilhas, jardinagem e artesanato. O objetivo é promover a integração e a igualdade de oportunidades entre todos os visitantes, em um ambiente tranquilo e cheio de harmonia.', 'Estrada da Paz, S/N', 'Fazenda Harmonia', '(11) 1234-5678', '/companies/1/1.png', 1, 2);
INSERT INTO proprietario_table (id, descricao, logradouro, nome_estabelecimento, telefone, url_icone, endereco_id, usuarios_id) VALUES(2, 'A cafeteria Pão e Prosa é um ambiente acolhedor e inclusivo, pensado para receber pessoas de todas as habilidades e necessidades. Com cardápio variado, que inclui opções sem glúten e sem lactose, e ambiente acessível, a cafeteria busca proporcionar uma experiência agradável para todos os clientes. Além disso, a Pão e Prosa valoriza a diversidade e a inclusão, buscando promover um ambiente de convivência harmônica e respeitosa.', 'Rua dos Poetas, 123', 'Pão e Prosa', '(11) 5555-5555', '/companies/2/1.png', 5, 4);
INSERT INTO proprietario_table (id, descricao, logradouro, nome_estabelecimento, telefone, url_icone, endereco_id, usuarios_id) VALUES(3, '"Sabores do Interior" é um restaurante acolhedor no bairro de Copacabana, Rio de Janeiro. Oferecemos acessibilidade física para facilitar o acesso de todos os clientes. Além disso, disponibilizamos cardápios em braile para clientes com deficiência visual e um sistema de som adaptado para atender às necessidades de pessoas com deficiência auditiva.
INSERT INTO proprietario_table (id, descricao, logradouro, nome_estabelecimento, telefone, url_icone, endereco_id, usuarios_id) VALUES(4, '"Parque dos Ipês" é um espaço encantador e inclusivo em Campinas, São Paulo. Proporcionamos momentos memoráveis para todos os visitantes. O parque é totalmente acessível, com recursos para deficientes visuais e áreas adequadas para idosos com 80 anos ou mais. Desfrute da beleza natural e da alegria em nosso parque! Venha nos visitar e aproveitar a tranquilidade e diversão em meio aos ipês!', 'Avenida das Flores, 456', 'Parque dos Ipês', '(19) 8765-4321', '/companies/4/1.png', 7, 6);
INSERT INTO proprietario_table (id, descricao, logradouro, nome_estabelecimento, telefone, url_icone, endereco_id, usuarios_id) VALUES(5, 'O "Rancho do Vô João" é um encantador refúgio em Belo Horizonte, MG. Nosso estabelecimento é totalmente acessível, oferecendo recursos para deficientes visuais e áreas adequadas para idosos com 80 anos ou mais. Desfrute de momentos especiais com a família e amigos em meio à tranquilidade do nosso rancho acolhedor! Venha nos visitar e compartilhar experiências memoráveis!', 'Estrada do Sertão, Km 10', 'Rancho do Vô João', '(31) 9876-5432', '/companies/5/1.png', 8, 7);
INSERT INTO proprietario_table (id, descricao, logradouro, nome_estabelecimento, telefone, url_icone, endereco_id, usuarios_id) VALUES(6, 'O "Parque Aquático Splash" é o local perfeito para diversão em família e amigos no Rio de Janeiro, RJ. Nossa estrutura é totalmente acessível, com recursos para proporcionar uma experiência inclusiva. Desfrute de um dia refrescante e repleto de emoção em nosso parque aquático acolhedor! Venha se divertir e criar lembranças inesquecíveis!', 'Avenida das Águas, 123', 'Parque Aquático Splash', '(21) 9876-5432', '/companies/6/1.png', 4, 8);
INSERT INTO proprietario_table (id, descricao, logradouro, nome_estabelecimento, telefone, url_icone, endereco_id, usuarios_id) VALUES(7, 'A "Fazenda do Vale" é um refúgio encantador em Campinas, SP. Nosso estabelecimento é totalmente acessível, oferecendo recursos para tornar a experiência inclusiva para pessoas de todas as idades. Desfrute de momentos especiais com a família e amigos em meio à natureza exuberante e acolhedora da nossa fazenda! Venha nos visitar e criar memórias inesquecíveis!', 'Estrada Rural, Km 20', 'Fazenda do Vale', '(19) 8765-4321', '/companies/7/1.png', 9, 9);
INSERT INTO proprietario_table (id, descricao, logradouro, nome_estabelecimento, telefone, url_icone, endereco_id, usuarios_id) VALUES(8, 'O "Espaço Cultural Atlântico" é um lugar único no coração do Centro Histórico do Rio de Janeiro, RJ. Nosso espaço é totalmente acessível, oferecendo recursos para tornar a experiência inclusiva e enriquecedora para todas as pessoas. Desfrute de eventos culturais diversos, exposições e apresentações em um ambiente acolhedor e inspirador. Venha nos visitar e explorar a riqueza cultural do nosso espaço!', 'Avenida dos Artistas, 789', 'Espaço Cultural Atlântico', '(21) 9876-5432', '/companies/8/1.png', 10, 10);
